INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (1958, 1, '2015-10-12 00:02:04', '2015-10-12 04:02:04', '<strong>Marvel Star Wars #1 - Zapp Comics Exclusive Cover</strong>\r\n\r\n<strong>Variant Cover by Mike Mayhew</strong>\r\n\r\nHomage to New Mutants #98 Cover by Rob Liefeld\r\n\r\nWritten by Jason Aaron. Art by John Cassaday.\r\n\r\nRelease Date - 1/14/15\r\n\r\n----------------------------------------------------------------\r\n\r\n<strong>THE GREATEST SPACE ADVENTURE OF ALL TIME RETURNS TO MARVEL</strong>! Luke Skywalker and the ragtag band of rebels fighting against the Galactic Empire are fresh off their biggest victory yet-the destruction of the massive battle station known as the Death Star. But the Empire\'s not toppled yet! Join Luke along with Princess Leia, smugglers Han Solo and Chewbacca, droids C-3PO and R2-D2 and the rest of the Rebel Alliance as they strike out for freedom against the evil forces of Darth Vader and his master, the Emperor. This is the Star Wars saga as only Marvel Comics could make it!\r\n\r\n48 pages, full color.\r\n\r\nRated T', 'Marvel Star Wars #1 - Zapp Comics Exclusive', 'Marvel Star Wars #1 - Zapp Comics Exclusive', 'publish', 'closed', 'closed', '', 'product-1', '', '', '2015-11-01 09:25:49', '2015-11-01 13:25:49', '', 0, 'http://192.168.9.40/_2014/_04-April/woocommerce_upd/product/product-15/', 0, 'product', '', 0); 
