INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2493, 1, '2015-10-12 09:32:28', '2015-10-12 13:32:28', '<h3>Welcome to ZAPP COMICS\' RADIO PODCAST.</h3>\r\n<strong>Straight from the comic shop to your ears!</strong>\r\n\r\nJoin shop owner Corry and his ridiculous cast of characters as they discuss current hot topics in nerd culture and look back at the comics, movies, TV shows, and video games that made them the nerds they are today.\r\n\r\nCheck out out playlist of our latest episodes below and <strong>don\'t forget to listen to us</strong> on the following platforms.\r\n<ul>\r\n	<li><strong><a href="https://audioboom.com/ZappComicsRadio" target="_blank">audiBoom</a></strong></li>\r\n	<li><strong><a href="https://itunes.apple.com/al/podcast/zapp-comics-radio-podcast/id981949976?mt=2" target="_blank">iTunes</a></strong></li>\r\n	<li><strong><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></strong></li>\r\n</ul>\r\n&nbsp;\r\n\r\nhttps://www.youtube.com/watch?v=SggvQYzc79U&list=PL08WwYPXItWwKoMhca3Uioy787eJv-QA6&index=18', 'ZAPP COMICS RADIO PODCAST', '<h2>Like Podcasts? We got one! It\'s like comics for your ears. Give your eyes a break and Listen In!</h2>', 'inherit', 'closed', 'closed', '', '2297-revision-v1', '', '', '2015-10-12 09:32:28', '2015-10-12 13:32:28', '', 2297, 'http://www.zappcomics.com/uncategorized/2297-revision-v1/', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2494, 1, '2015-10-12 09:38:32', '2015-10-12 13:38:32', '<img class="alignnone size-full wp-image-2417" src="http://www.zappcomics.com/wp-content/uploads/2011/07/ZAPP-BUYS-WEB.jpg" alt="ZAPP BUYS WEB" width="1171" height="278" />\r\n<h1>Get Top Dollar for Your Collection!</h1>\r\nhttps://www.youtube.com/watch?v=TjmqNQm9WH0\r\n<h1>What Does Zapp Buy?</h1>\r\n<h4>Looking to sell your entire comic book collection or specific key issues? Zapp buys ALL comic book collections from the 1930’s to present, trade paperbacks, graphic novels, comics-related magazines, action figures, store or dealer inventories, warehouse finds, and much more!</h4>\r\n&nbsp;\r\n<h1>Why Sell Your Comics to Zapp?</h1>\r\n<h4>As an Overstreet Price Guide Advisor, Zapp Comics owner Ben L. brings 27 years of expertise and professionalism to every transaction. With a friendly, no pressure approach to buying and selling, Ben has an impeccable reputation for integrity and honesty…just ask around! Owner of the best comic book stores in New Jersey, Ben stands behind every deal.</h4>\r\n<h4>Cash is available for any size deal; no consignments or waiting for your money. Ben acts quickly and has bought many, many collections due to other buyers simply not following-up. Zapp shows up and spends.</h4>\r\n&nbsp;\r\n<h1>Where Do We Buy Our Comics?</h1>\r\n<h4>Ben has traveled to New Jersey, Pennsylvania, New York, Connecticut, Delaware, Maryland, Vermont and Ohio. He has has also bought from as far away as California and Florida. No state is too far for the right collection.</h4>\r\n&nbsp;\r\n<h1>Contact Us Now!</h1>\r\n<h4>We\'ll respond quickly! Please use the contact form below, or...</h4>\r\n<h4>[address]\r\n<span class="contacts_email">E-mail: <a href="mailto:ben@zappcomics.com">ben@zappcomics.com</a></span>\r\n<span class="contacts_phone">Telephone: +1 973 727 6171</span>\r\n[/address]</h4>\r\n<h4></h4>\r\n<h4>[span8]</h4>\r\n<h1>Tell us about your collection...</h1>\r\n[contact-form-7 id="208" title="Cherry contact form"]\r\n[/span8]', 'We Buy Comics', '', 'inherit', 'closed', 'closed', '', '14-revision-v1', '', '', '2015-10-12 09:38:32', '2015-10-12 13:38:32', '', 14, 'http://www.zappcomics.com/uncategorized/14-revision-v1/', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2495, 1, '2015-10-12 09:41:44', '2015-10-12 13:41:44', '<h3>Welcome to ZAPP COMICS\' TV Channel - ZAPP TALK!</h3>\r\n<strong>Shop Owner Corry and Dave are here to deliver your weekly comic news whether you like it or not! (but trust us, you\'ll like it)</strong>\r\n\r\nZapp Talk is our weekly episodic video segment and our goal is to keep you abreast of all the new releases from your buddies at Marvel, DC, Image, Dark Horse, Kaboom, Diamond, Neca, IDW and more!!!\r\n\r\nCorry and Dave have their thumbs on the throat of comic book and nerd culture, and at any second they can squeeze and end it<span class="text_exposed_show"> all!</span>\r\n\r\nThat may or may not be the truth, them being handsome only makes it more fun to watch.\r\n\r\nCheck out our playlist of our latest episodes below and <strong>don\'t forget to watch to us</strong> on the following platforms.\r\n<ul>\r\n	<li><b><a href="https://www.facebook.com/ZappTalk" target="_blank">Facebook</a></b></li>\r\n	<li><strong><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></strong></li>\r\n</ul>\r\nhttps://www.youtube.com/playlist?list=PL08WwYPXItWzA0Yi1j4QbYItDEEmeQ3zq', 'ZAPP TALK', '<h2>Zapp Talk is a weekly video segment brought to you by the team at Zapp Comics. Check it out, we promise you\'ll enjoy it!</h2>', 'inherit', 'closed', 'closed', '', '1910-revision-v1', '', '', '2015-10-12 09:41:44', '2015-10-12 13:41:44', '', 1910, 'http://www.zappcomics.com/uncategorized/1910-revision-v1/', 0, 'revision', '', 0); 
