INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2521, 1, '2015-10-14 14:32:11', '2015-10-14 18:32:11', '<h3>Welcome to ZAPP COMICS\' TV Channel - ZAPP TALK!</h3>\r\n<strong>Shop Owner Corry and Dave are here to deliver your weekly comic news whether you like it or not! (but trust us, you\'ll like it)</strong>\r\n\r\nZapp Talk is&nbsp;our&nbsp;weekly episodic video segment and our goal is to keep you abreast of all the new releases from your buddies at Marvel, DC, Image, Dark Horse, Kaboom, Diamond, Neca, IDW and more!!!\r\n\r\nCorry and Dave have their thumbs on the throat of comic book and nerd culture, and at any second they can squeeze and end it<span class="text_exposed_show"> all!</span>\r\n\r\nThat may or may not be the truth, them being handsome only makes it more fun to watch.\r\n\r\nCheck out our playlist of our latest episodes below and <strong>don\'t forget to watch to us</strong> on the following platforms.\r\n<ul>\r\n	<li><b><a href="https://www.facebook.com/ZappTalk" target="_blank">Facebook</a></b></li>\r\n	<li><strong><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></strong></li>\r\n</ul>\r\nhttps://www.youtube.com/playlist?list=PL08WwYPXItWzA0Yi1j4QbYItDEEmeQ3zq', 'ZAPP TALK', '<h2>Zapp Talk is a weekly video segment brought to you by the team at Zapp Comics. Check it out, we promise you\'ll enjoy it!</h2>', 'inherit', 'closed', 'closed', '', '1910-revision-v1', '', '', '2015-10-14 14:32:11', '2015-10-14 18:32:11', '', 1910, 'http://www.zappcomics.com/uncategorized/1910-revision-v1/', 0, 'revision', '', 0); 
