INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2615, 1, '2015-10-24 15:11:59', '2015-10-24 19:11:59', '<h1>Welcome to ZAPP COMICS\' TV Channel - ZAPP TALK!</h1>\r\n&nbsp;\r\n<h4 style="padding-left: 30px;"><strong>Shop Owner Corry and Dave are here to deliver your weekly comic news whether you like it or not! (but trust us, you\'ll like it)</strong></h4>\r\n<h4 style="padding-left: 30px;">Zapp Talk is our weekly episodic video segment and our goal is to keep you abreast of all the new releases from your buddies at Marvel, DC, Image, Dark Horse, Kaboom, Diamond, Neca, IDW and more!!!</h4>\r\n<h4 style="padding-left: 30px;">Corry and Dave have their thumbs on the throat of comic book and nerd culture, and at any second they can squeeze and end it<span class="text_exposed_show"> all!</span></h4>\r\n<h4 style="padding-left: 30px;">That may or may not be the truth, them being handsome only makes it more fun to watch.</h4>\r\n<h4 style="padding-left: 30px;">Check out our playlist of our latest episodes below and <strong>don\'t forget to watch to us</strong> on the following platforms.</h4>\r\n<h4 style="padding-left: 30px;"><a href="https://www.facebook.com/ZappTalk" target="_blank">Facebook</a></h4>\r\n<h4 style="padding-left: 30px;"><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></h4>\r\n&nbsp;\r\n\r\nhttps://www.youtube.com/playlist?list=PL08WwYPXItWzA0Yi1j4QbYItDEEmeQ3zq\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'ZAPP TALK', '<h2>Zapp Talk is a weekly video segment brought to you by the team at Zapp Comics. Check it out, we promise you\'ll enjoy it!</h2>', 'inherit', 'closed', 'closed', '', '1910-revision-v1', '', '', '2015-10-24 15:11:59', '2015-10-24 19:11:59', '', 1910, 'http://www.zappcomics.com/uncategorized/1910-revision-v1/', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2616, 1, '2015-10-24 15:14:03', '2015-10-24 19:14:03', '<h1>You ever want to work at a comic shop?</h1>\r\n&nbsp;\r\n<h4 style="padding-left: 30px;">Dave has adapted a guerrilla journalistic view on his day to day adventures at Zapp Comics. All of your pulse pounding questions will be answered!</h4>\r\n<h4 style="padding-left: 30px;">Who is Corry Brown really? If a long box falls in the back room, who has to pick it up? Where do 90’s hologram covers go to die? <strong>FIND OUT HERE!</strong></h4>\r\n<h4 style="padding-left: 30px;">Check out our playlist of our latest episodes below and <strong>don\'t forget to watch to us</strong> on the following platforms.</h4>\r\n<h4 style="padding-left: 30px;"><b><a href="https://www.facebook.com/ZappTalk" target="_blank">Facebook</a></b></h4>\r\n<h4 style="padding-left: 30px;"><strong><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></strong></h4>\r\n&nbsp;\r\n\r\nhttps://www.youtube.com/playlist?list=PL08WwYPXItWyGyalx1jXJDoAKmL2veDRI\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'COMIC JOCKEYS', '<h2>Who is Corry Brown really? If a long box falls in the back room, who has to pick it up? Where do 90’s hologram covers go to die? FIND OUT HERE!</h2>', 'inherit', 'closed', 'closed', '', '2341-revision-v1', '', '', '2015-10-24 15:14:03', '2015-10-24 19:14:03', '', 2341, 'http://www.zappcomics.com/uncategorized/2341-revision-v1/', 0, 'revision', '', 0); 
