INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2627, 1, '2015-10-24 23:51:53', '2015-10-25 03:51:53', '<h1>HALLOWEEN COMIC FEST IS HERE</h1>\r\n<h4></h4>\r\n&nbsp;\r\n<h4 style="padding-left: 30px;"><strong>FREE COMICS, CANDY, AND SKETCHES!</strong></h4>\r\n<h4 style="text-align: justify; padding-left: 30px;">Come by Zapp Comics this Halloween to celebrate the season with&nbsp;exclusive free comics!</h4>\r\n<h4 style="text-align: justify; padding-left: 30px;">Local artist Nick Justus will be doing free sketches with the purchase of any sketch cover at our Wayne location.</h4>\r\n<h4 style="text-align: justify; padding-left: 30px;">Before you hit the streets for the free candy, stop in at your local shop!</h4>\r\n<h4 style="text-align: justify; padding-left: 30px;">I dare you...</h4>\r\n<h4 style="text-align: justify; padding-left: 30px;">For more details go to <a href="http://www.halloweencomicfest.com/Home/1/1/78/1138" target="_blank">HalloweenComicFest.com</a></h4>', 'Halloween Comic Fest', '<h2>Halloween Comic Fest Is Here! Free Comics, Candy and Sketches!</h2><br><br>\r\n\r\n', 'inherit', 'closed', 'closed', '', '2588-revision-v1', '', '', '2015-10-24 23:51:53', '2015-10-25 03:51:53', '', 2588, 'http://www.zappcomics.com/uncategorized/2588-revision-v1/', 0, 'revision', '', 0); 
