INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2723, 1, '2015-11-09 07:38:54', '2015-11-09 11:38:54', '<h1>Welcome to ZAPP COMICS\' TV Channel - ZAPP TALK!</h1>\r\n&nbsp;\r\n<h4 style="padding-left: 30px;"><strong>Shop Owner Corry and Dave are here to deliver your weekly comic news whether you like it or not! (but trust us, you\'ll like it)</strong></h4>\r\n<h4 style="padding-left: 30px;">Zapp Talk is our weekly episodic video segment and our goal is to keep you abreast of all the new releases from your buddies at Marvel, DC, Image, Dark Horse, Kaboom, Diamond, Neca, IDW and more!!!</h4>\r\n<h4 style="padding-left: 30px;">Corry and Dave have their thumbs on the throat of comic book and nerd culture, and at any second they can squeeze and end it<span class="text_exposed_show"> all!</span></h4>\r\n<h4 style="padding-left: 30px;">That may or may not be the truth, them being handsome only makes it more fun to watch.</h4>\r\n<h4 style="padding-left: 30px;">Check out our playlist of our latest episodes below and <strong>don\'t forget to watch to us</strong> on the following platforms.</h4>\r\n&nbsp;\r\n<h4 style="padding-left: 30px;"><strong><a href="https://www.facebook.com/ZappTalk" target="_blank">Facebook</a></strong></h4>\r\n<h4 style="padding-left: 30px;"><strong><a href="https://www.youtube.com/user/ZAPPcomicsNJ" target="_blank">YouTube</a></strong></h4>\r\n&nbsp;\r\n\r\nhttps://www.youtube.com/playlist?list=PL08WwYPXItWzA0Yi1j4QbYItDEEmeQ3zq', 'ZAPP TALK', '<h2>Zapp Talk is a weekly video segment brought to you by the team at Zapp Comics. Check it out, we promise you\'ll enjoy it!</h2>', 'inherit', 'closed', 'closed', '', '1910-revision-v1', '', '', '2015-11-09 07:38:54', '2015-11-09 11:38:54', '', 1910, 'http://www.zappcomics.com/uncategorized/1910-revision-v1/', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_wj8crgq5gf_posts` VALUES (2724, 1, '2015-11-09 07:42:41', '2015-11-09 11:42:41', '<img class="alignnone size-full wp-image-2417" src="http://www.zappcomics.com/wp-content/uploads/2011/07/ZAPP-BUYS-WEB.jpg" alt="ZAPP BUYS WEB" width="1171" height="278" />\r\n<!--\r\n<h1>Get Top Dollar for Your Collection!</h1>\r\n-->\r\n<!--https://www.youtube.com/watch?v=TjmqNQm9WH0-->\r\n<h1>What Does Zapp Buy?</h1>\r\n<h3 style="padding-left: 30px;">Looking to sell your entire comic book collection or specific key issues? Zapp buys ALL comic book collections from the 1930’s to present, trade paperbacks, graphic novels, comics-related magazines, action figures, store or dealer inventories, warehouse finds, and much more!</h3>\r\n&nbsp;\r\n<h1>Why Sell Your Comics to Zapp?</h1>\r\n<h3 style="padding-left: 30px;">As an Overstreet Price Guide Advisor, Zapp Comics owner Ben L. brings 27 years of expertise and professionalism to every transaction. With a friendly, no pressure approach to buying and selling, Ben has an impeccable reputation for integrity and honesty…just ask around! Owner of the best comic book stores in New Jersey, Ben stands behind every deal.</h3>\r\n<h3 style="padding-left: 30px;">Cash is available for any size deal; no consignments or waiting for your money. Ben acts quickly and has bought many, many collections due to other buyers simply not following-up. Zapp shows up and spends.</h3>\r\n&nbsp;\r\n<h1>Where Do We Buy Our Comics?</h1>\r\n<h3 style="padding-left: 30px;">Ben has traveled to New Jersey, Pennsylvania, New York, Connecticut, Delaware, Maryland, Vermont and Ohio. He has has also bought from as far away as California and Florida. No state is too far for the right collection.</h3>\r\n&nbsp;\r\n<h1>Contact Us Now!</h1>\r\n<h3 style="padding-left: 30px;">We\'ll respond quickly! Please use the contact form below, or...</h3>\r\n<h3 style="padding-left: 30px;">[address]\r\n<span class="contacts_email">E-mail: <a href="mailto:ben@zappcomics.com">ben@zappcomics.com</a></span>\r\n<span class="contacts_phone">Telephone: +1 973 727 6171</span>\r\n[/address]</h3>\r\n<h4></h4>\r\n<h4>[span8]</h4>\r\n<h1>Tell us about your collection...</h1>\r\n[contact-form-7 id="208" title="Cherry contact form"]\r\n[/span8]', 'We Buy Comics', '', 'inherit', 'closed', 'closed', '', '14-revision-v1', '', '', '2015-11-09 07:42:41', '2015-11-09 11:42:41', '', 14, 'http://www.zappcomics.com/uncategorized/14-revision-v1/', 0, 'revision', '', 0); 
